INSERT INTO `wp_term_taxonomy` VALUES (1,1,'category','',0,6),(2,2,'nav_menu','',0,8),(3,3,'category','Versos e poemas para, como uma fotografia, captar e imortalizar momentos. Ângulos nunca vistos ou esquecidos. Temas simples e outros de complexidade necessária que transpirados em versos tem como intento devolver-nos a nós mesmo.\r\n\r\nNão há uma escolha específica de temas aqui. Os versos e poemas publicados são escolhidos conforme minhas angústias. Em outros momentos serão escolhidos com apelo ao transbordar que muitas vezes penso necessário para que eu viva.\r\n\r\nEsperamos que todo tipo de sensação seja causada ao ler estes versos e que sejam eles momentos necessários de reflexão ou relaxamento.',0,13),(4,4,'category','',0,0),(5,5,'category','',4,0),(6,6,'category','',4,0),(7,7,'category','',4,0);
